import React, { useState } from "react";

export default function QuantumSimulator() {
  const [bit1, setBit1] = useState("0");
  const [bit2, setBit2] = useState("0");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  async function runSimulation() {
    setLoading(true);
    const response = await fetch("http://localhost:5000/simulate", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ bit1, bit2 }),
    });
    const data = await response.json();
    setResult(data);
    setLoading(false);
  }

  return (
    <div style={{ maxWidth: 400, margin: "auto", padding: "2rem", fontFamily: "Arial" }}>
      <h2>Superdense Coding Simulator</h2>
      <div>
        <label>Bit 1: </label>
        <select value={bit1} onChange={e => setBit1(e.target.value)}>
          <option value="0">0</option>
          <option value="1">1</option>
        </select>
      </div>
      <div>
        <label>Bit 2: </label>
        <select value={bit2} onChange={e => setBit2(e.target.value)}>
          <option value="0">0</option>
          <option value="1">1</option>
        </select>
      </div>
      <button onClick={runSimulation} disabled={loading} style={{ marginTop: "1rem" }}>
        {loading ? "Simulating..." : "Simulate"}
      </button>
      {result && (
        <div style={{ marginTop: "1rem" }}>
          <p>Original bits: {result.original_bits}</p>
          <p>Measured bits: {result.measured_bits}</p>
        </div>
      )}
    </div>
  );
}
